import React, { useState } from 'react';

function StudentManagement() {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({ name: '', score: '' });

  const addStudent = () => {
    if (newStudent.name && newStudent.score) {
      setStudents([...students, newStudent]);
      setNewStudent({ name: '', score: '' });
    }
  };

  return (
    <div className="bg-white shadow-md rounded p-4">
      <div className="mb-4">
        <input
          type="text"
          placeholder="Tên sinh viên"
          value={newStudent.name}
          onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
          className="border p-2 mr-2 rounded"
        />
        <input
          type="number"
          placeholder="Điểm"
          value={newStudent.score}
          onChange={(e) => setNewStudent({ ...newStudent, score: e.target.value })}
          className="border p-2 mr-2 rounded"
        />
        <button onClick={addStudent} className="bg-blue-500 text-white px-4 py-2 rounded">
          Thêm
        </button>
      </div>
      <table className="table-auto w-full border-collapse border border-gray-300">
        <thead>
          <tr>
            <th className="border border-gray-300 px-4 py-2">Tên</th>
            <th className="border border-gray-300 px-4 py-2">Điểm</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student, index) => (
            <tr key={index}>
              <td className="border border-gray-300 px-4 py-2">{student.name}</td>
              <td className="border border-gray-300 px-4 py-2">{student.score}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default StudentManagement;
