import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { BookOpenIcon } from '@heroicons/react/24/outline';

function DanhSachKhoa() {
  const [departments, setDepartments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('https://apiwebsa.onrender.com/api/khoa')
      .then((response) => response.json())
      .then((data) => setDepartments(data))
      .catch((error) => console.error('Error fetching departments:', error));
  }, []);

  const handleDepartmentClick = (khoaId) => {
    navigate(`/khoa/${khoaId}`);
  };

  return (
    <div className="max-w-6xl mx-auto p-6 min-h-screen bg-gradient-to-br from-blue-50 to-white">
      <div className="flex items-center justify-center mb-6">
        <BookOpenIcon className="w-8 h-8 text-blue-600 mr-2" />
        <h1 className="text-3xl font-bold text-blue-700">Danh Sách Các Khoa</h1>
      </div>

      <div className="overflow-x-auto rounded-lg shadow-lg bg-white">
        <table className="min-w-full text-sm">
          <thead className="bg-blue-500 text-white">
            <tr>
              <th className="px-6 py-3 text-left">STT</th>
              <th className="px-6 py-3 text-left">Mã Khoa</th>
              <th className="px-6 py-3 text-left">Tên Khoa</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {departments.map((department, index) => (
              <tr
                key={department.id}
                className="hover:bg-blue-50 cursor-pointer"
                onClick={() => handleDepartmentClick(department.khoa_id)}
              >
                <td className="px-6 py-4">{index + 1}</td>
                <td className="px-6 py-4 font-medium text-blue-700">{department.khoa_id}</td>
                <td className="px-6 py-4">{department.tenkhoa}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default DanhSachKhoa;
