import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function DanhSachLop() {
  const { khoaId } = useParams();
    const [tenkhoa, setTenkhoa] = useState('');
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    fetch(`https://apiwebsa.onrender.com/api/lophoc?khoa_id=${khoaId}`)
      .then((response) => response.json())
      .then((data) => setClasses(data))
      .catch((error) => console.error('Error fetching classes:', error));
  }, [khoaId],[tenkhoa]);

  return (
    <div className="max-w-6xl mx-auto p-6 min-h-screen bg-gradient-to-br from-green-50 to-white">
      <h1 className="text-3xl font-bold text-green-700 text-center mb-6">
        Danh Sách Lớp Học - Khoa {tenkhoa}
      </h1>
      <div className="overflow-x-auto rounded-lg shadow-lg bg-white">
        <table className="min-w-full text-sm">
          <thead className="bg-green-500 text-white">
            <tr>
              <th className="px-6 py-3 text-left">STT</th>
              <th className="px-6 py-3 text-left">Mã Lớp</th>
              <th className="px-6 py-3 text-left">Lớp Học</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {classes.map((lop, index) => (
              <tr key={lop.id} className="hover:bg-green-50">
                <td className="px-6 py-4">{index + 1}</td>
                <td className="px-6 py-4 font-medium text-green-700">{lop.lophoc_id}</td>
                <td className="px-6 py-4">{lop.phonghoc}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default DanhSachLop;
