import React, { useState, useEffect } from 'react';

function StudentManagement() {
  const [students, setStudents] = useState([]);
  const [departments, setDepartments] = useState([]); // State to store departments
  const [newStudent, setNewStudent] = useState({ id: '', name: '', dob: '', gender: 'Nam', midterm: '', final: '', department: '' });

  // Fetch students from API
  useEffect(() => {
    fetch('https://apiwebsa.onrender.com/api/khoa')
      .then((response) => response.json())
      .then((data) => setDepartments(data)) // Load departments
      .catch((error) => console.error('Error fetching departments:', error));
  }, []);

  const addStudent = () => {
    if (newStudent.id && newStudent.name && newStudent.dob && newStudent.midterm && newStudent.final && newStudent.department) {
      const studentToAdd = { ...newStudent, midterm: parseFloat(newStudent.midterm), final: parseFloat(newStudent.final) };

      fetch('https://apiwebsa.onrender.com/api/khoa', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(studentToAdd),
      })
        .then((response) => response.json())
        .then((addedStudent) => {
          setStudents([...students, addedStudent]);
          setNewStudent({ id: '', name: '', dob: '', gender: 'Nam', midterm: '', final: '', department: '' });
        })
        .catch((error) => console.error('Error adding student:', error));
    }
  };

  return (
    <div className="max-w-6xl mx-auto p-6 bg-gray-100 min-h-screen">
      <div className="bg-white shadow-lg rounded-lg p-6 mb-8">
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-6">
          <input type="text" placeholder="MSSV" value={newStudent.id} onChange={(e) => setNewStudent({ ...newStudent, id: e.target.value })} className="border p-2 rounded-md" />
          <input type="text" placeholder="Tên" value={newStudent.name} onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })} className="border p-2 rounded-md" />
          <input type="date" value={newStudent.dob} onChange={(e) => setNewStudent({ ...newStudent, dob: e.target.value })} className="border p-2 rounded-md" />
          <select value={newStudent.gender} onChange={(e) => setNewStudent({ ...newStudent, gender: e.target.value })} className="border p-2 rounded-md">
            <option value="Nam">Nam</option>
            <option value="Nữ">Nữ</option>
          </select>
          <input type="number" placeholder="Điểm GK" value={newStudent.midterm} onChange={(e) => setNewStudent({ ...newStudent, midterm: e.target.value })} className="border p-2 rounded-md" />
          <input type="number" placeholder="Điểm Cuối Kỳ" value={newStudent.final} onChange={(e) => setNewStudent({ ...newStudent, final: e.target.value })} className="border p-2 rounded-md" />
          <select value={newStudent.department} onChange={(e) => setNewStudent({ ...newStudent, department: e.target.value })} className="border p-2 rounded-md">
            <option value="">Chọn Khoa</option>
            {departments.map((department) => (
              <option key={department.id} value={department.name}>
                {department.name}
              </option>
            ))}
          </select>
        </div>
        <button onClick={addStudent} className="bg-blue-500 text-white px-6 py-2 rounded-md shadow-md hover:bg-blue-600 transition">Thêm</button>
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full bg-white shadow-md rounded-lg">
          <thead className="bg-blue-500 text-white">
            <tr>
              <th className="px-6 py-3">STT</th>
              <th className="px-6 py-3">MSSV</th>
              <th className="px-6 py-3">Tên</th>
              <th className="px-6 py-3">Ngày Sinh</th>
              <th className="px-6 py-3">Giới Tính</th>
              <th className="px-6 py-3">Điểm GK</th>
              <th className="px-6 py-3">Điểm Cuối Kỳ</th>
              <th className="px-6 py-3">Khoa</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {students.map((student, index) => (
              <tr key={index} className="hover:bg-gray-50">
                <td className="px-6 py-4 text-center">{index + 1}</td>
                <td className="px-6 py-4">{student.id}</td>
                <td className="px-6 py-4 font-medium">{student.name}</td>
                <td className="px-6 py-4">{student.dob}</td>
                <td className="px-6 py-4 text-center">{student.gender}</td>
                <td className="px-6 py-4 text-center">{student.midterm}</td>
                <td className="px-6 py-4 text-center">{student.final}</td>
                <td className="px-6 py-4 text-center">{student.department}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default StudentManagement;
