import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import DanhSachKhoa from './components/DanhSachKhoa';
import DanhSachLop from './components/DanhSachLop';
import Login from './components/Login';

function App() {
  useEffect(() => {
    // Automatically login and fetch token
    fetch('https://apiwebsa.onrender.com/api/auth/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username: 'admin1', password: 'pass789' }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Login failed');
        }
        return response.json();
      })
      .then((data) => {
        localStorage.setItem('token', data.token); // Save token to localStorage
      })
      .catch((error) => console.error('Error logging in:', error));
  }, []);

  return (
    <Router>
      <div className="min-h-screen bg-gray-100 p-4">
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/khoa" element={<DanhSachKhoa />} />
          <Route path="/khoa/:khoaId" element={<DanhSachLop />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
