import React, { useState } from 'react';

function Login() {
  const [credentials, setCredentials] = useState({ user: '', password: '' });
  const [token, setToken] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    fetch('https://apiwebsa.onrender.com/api/auth/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(credentials),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Login failed');
        }
        return response.json();
      })
      .then((data) => {
        setToken(data.token); // Save the token
        setError('');
      })
      .catch((err) => {
        setError(err.message);
        setToken('');
      });
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow-lg rounded-lg">
      <h1 className="text-2xl font-bold text-center mb-4">Đăng Nhập</h1>
      <div className="mb-4">
        <label className="block text-sm font-medium mb-1">Tài khoản</label>
        <input
          type="text"
          value={credentials.user}
          onChange={(e) => setCredentials({ ...credentials, user: e.target.value })}
          className="w-full border p-2 rounded-md"
        />
      </div>
      <div className="mb-4">
        <label className="block text-sm font-medium mb-1">Mật khẩu</label>
        <input
          type="password"
          value={credentials.password}
          onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
          className="w-full border p-2 rounded-md"
        />
      </div>
      <button
        onClick={handleLogin}
        className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 transition"
      >
        Đăng Nhập
      </button>
      {error && <p className="text-red-500 mt-4">{error}</p>}
      {token && (
        <div className="mt-4 p-4 bg-green-100 text-green-700 rounded-md">
          <p>Token:</p>
          <code className="break-words">{token}</code>
        </div>
      )}
    </div>
  );
}

export default Login;
