import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function GiangVienDashboard() {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Dữ liệu mẫu cho môn học
  const sampleCourses = [
    { id: 1, name: 'Lập trình Web', code: 'WEB101', semester: 'HK1-2023-2024' },
    { id: 2, name: 'Cơ sở dữ liệu', code: 'DBS101', semester: 'HK1-2023-2024' },
    { id: 3, name: 'Lập trình Java', code: 'JAV101', semester: 'HK1-2023-2024' }
  ];

  // Dữ liệu mẫu cho sinh viên
  const sampleStudents = {
    1: [
      { id: 1, mssv: '20001', hoten: 'Nguyễn Văn A', diemGK: 8.5, diemCK: 7.5 },
      { id: 2, mssv: '20002', hoten: 'Trần Thị B', diemGK: 7.0, diemCK: 8.0 },
      { id: 3, mssv: '20003', hoten: 'Lê Văn C', diemGK: 9.0, diemCK: 8.5 }
    ],
    2: [
      { id: 4, mssv: '20004', hoten: 'Phạm Thị D', diemGK: 8.0, diemCK: 8.0 },
      { id: 5, mssv: '20005', hoten: 'Hoàng Văn E', diemGK: 7.5, diemCK: 7.0 }
    ],
    3: [
      { id: 6, mssv: '20006', hoten: 'Vũ Thị F', diemGK: 9.5, diemCK: 9.0 },
      { id: 7, mssv: '20007', hoten: 'Đặng Văn G', diemGK: 8.0, diemCK: 8.5 }
    ]
  };

  useEffect(() => {
    // Giả lập API call
    const fetchCourses = async () => {
      try {
        setLoading(true);
        // Giả lập delay network
        await new Promise(resolve => setTimeout(resolve, 1000));
        setCourses(sampleCourses);
        setLoading(false);
      } catch (err) {
        setError('Không thể tải danh sách môn học');
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  const handleCourseSelect = async (courseId) => {
    try {
      setLoading(true);
      setSelectedCourse(courseId);
      // Giả lập delay network
      await new Promise(resolve => setTimeout(resolve, 500));
      setStudents(sampleStudents[courseId]);
      setLoading(false);
    } catch (err) {
      setError('Không thể tải danh sách sinh viên');
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/');
  };

  if (loading) return (
    <div className="flex justify-center items-center min-h-screen">
      <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-700"></div>
    </div>
  );

  if (error) return (
    <div className="text-center text-red-600 p-4">
      Error: {error}
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-2xl font-bold text-green-700">Giảng Viên Dashboard</h1>
            </div>
            <div className="flex items-center">
              <button
                onClick={handleLogout}
                className="ml-4 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
              >
                Đăng xuất
              </button>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {/* Danh sách môn học */}
        <div className="mb-8 grid grid-cols-1 md:grid-cols-3 gap-4">
          {courses.map(course => (
            <div
              key={course.id}
              onClick={() => handleCourseSelect(course.id)}
              className={`p-4 rounded-lg shadow cursor-pointer transition-all ${
                selectedCourse === course.id 
                  ? 'bg-green-100 border-2 border-green-500' 
                  : 'bg-white hover:bg-gray-50'
              }`}
            >
              <h3 className="font-bold text-lg text-gray-900">{course.name}</h3>
              <p className="text-sm text-gray-600">Mã môn: {course.code}</p>
              <p className="text-sm text-gray-600">Học kỳ: {course.semester}</p>
            </div>
          ))}
        </div>

        {/* Danh sách sinh viên */}
        {selectedCourse && students && (
          <div className="bg-white shadow overflow-hidden sm:rounded-lg">
            <div className="px-4 py-5 sm:px-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                Danh sách sinh viên - {courses.find(c => c.id === selectedCourse)?.name}
              </h3>
            </div>
            <div className="border-t border-gray-200">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      STT
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      MSSV
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Họ và tên
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Điểm GK
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Điểm CK
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {students.map((student, index) => (
                    <tr key={student.id} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {index + 1}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.mssv}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        {student.hoten}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.diemGK || '-'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {student.diemCK || '-'}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default GiangVienDashboard;
