import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function GiangVienDashboard() {
  const [subjects, setSubjects] = useState([]);
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [selectedSession, setSelectedSession] = useState(null);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Dữ liệu mẫu cho môn học và phòng học
  const sampleData = [
    {
      id: 1,
      name: 'Lập trình Web',
      code: 'WEB101',
      rooms: [
        {
          id: 'A101',
          name: 'Phòng A101',
          sessions: [
            { id: 1, time: 'Ca 1 (7:30 - 9:30)' },
            { id: 2, time: 'Ca 2 (9:45 - 11:45)' },
            { id: 3, time: 'Ca 3 (13:30 - 15:30)' }
          ]
        },
        {
          id: 'A102',
          name: 'Phòng A102',
          sessions: [
            { id: 1, time: 'Ca 1 (7:30 - 9:30)' },
            { id: 2, time: 'Ca 2 (9:45 - 11:45)' }
          ]
        }
      ]
    },
    {
      id: 2,
      name: 'Cơ sở dữ liệu',
      code: 'DBS101',
      rooms: [
        {
          id: 'B201',
          name: 'Phòng B201',
          sessions: [
            { id: 1, time: 'Ca 1 (7:30 - 9:30)' },
            { id: 3, time: 'Ca 3 (13:30 - 15:30)' }
          ]
        }
      ]
    }
  ];

  // Dữ liệu mẫu cho sinh viên theo ca học
  const sampleStudents = {
    'WEB101-A101-1': [
      { id: 1, mssv: '20001', hoten: 'Nguyễn Văn A', diemGK: 8.5, diemCK: 7.5 },
      { id: 2, mssv: '20002', hoten: 'Trần Thị B', diemGK: 7.0, diemCK: 8.0 }
    ],
    'WEB101-A101-2': [
      { id: 3, mssv: '20003', hoten: 'Lê Văn C', diemGK: 9.0, diemCK: 8.5 },
      { id: 4, mssv: '20004', hoten: 'Phạm Thị D', diemGK: 8.0, diemCK: 8.0 }
    ]
  };

  useEffect(() => {
    const fetchSubjects = async () => {
      try {
        setLoading(true);
        await new Promise(resolve => setTimeout(resolve, 1000));
        setSubjects(sampleData);
        setLoading(false);
      } catch (err) {
        setError('Không thể tải danh sách môn học');
        setLoading(false);
      }
    };

    fetchSubjects();
  }, []);

  const handleSubjectSelect = (subject) => {
    setSelectedSubject(subject);
    setSelectedRoom(null);
    setSelectedSession(null);
    setStudents([]);
  };

  const handleRoomSelect = (room) => {
    setSelectedRoom(room);
    setSelectedSession(null);
    setStudents([]);
  };

  const handleSessionSelect = async (sessionId) => {
    try {
      setLoading(true);
      const studentKey = `${selectedSubject.code}-${selectedRoom.id}-${sessionId}`;
      await new Promise(resolve => setTimeout(resolve, 500));
      setStudents(sampleStudents[studentKey] || []);
      setSelectedSession(sessionId);
      setLoading(false);
    } catch (err) {
      setError('Không thể tải danh sách sinh viên');
      setLoading(false);
    }
  };

  // ... existing loading and error handling ...

  return (
    <div className="min-h-screen bg-gray-50">
      {/* ... existing nav ... */}

      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {/* Danh sách môn học */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          {subjects.map(subject => (
            <div
              key={subject.id}
              onClick={() => handleSubjectSelect(subject)}
              className={`p-4 rounded-lg shadow cursor-pointer ${
                selectedSubject?.id === subject.id 
                  ? 'bg-blue-50 border-2 border-blue-500' 
                  : 'bg-white hover:bg-gray-50'
              }`}
            >
              <h3 className="font-bold text-lg">{subject.name}</h3>
              <p className="text-sm text-gray-600">Mã môn: {subject.code}</p>
            </div>
          ))}
        </div>

        {/* Danh sách phòng học */}
        {selectedSubject && (
          <div className="mt-6">
            <h3 className="text-lg font-medium mb-4">Phòng học - {selectedSubject.name}</h3>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              {selectedSubject.rooms.map(room => (
                <div
                  key={room.id}
                  onClick={() => handleRoomSelect(room)}
                  className={`p-4 rounded-lg shadow cursor-pointer ${
                    selectedRoom?.id === room.id
                      ? 'bg-green-50 border-2 border-green-500'
                      : 'bg-white hover:bg-gray-50'
                  }`}
                >
                  <h4 className="font-bold">{room.name}</h4>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Danh sách ca học */}
        {selectedRoom && (
          <div className="mt-6">
            <h3 className="text-lg font-medium mb-4">Ca học - {selectedRoom.name}</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {selectedRoom.sessions.map(session => (
                <button
                  key={session.id}
                  onClick={() => handleSessionSelect(session.id)}
                  className={`p-4 rounded-lg shadow ${
                    selectedSession === session.id
                      ? 'bg-indigo-600 text-white'
                      : 'bg-white hover:bg-gray-50'
                  }`}
                >
                  {session.time}
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Danh sách sinh viên */}
        {selectedSession && students.length > 0 && (
          <div className="mt-6 bg-white shadow overflow-hidden sm:rounded-lg">
            <div className="px-4 py-5 sm:px-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                Danh sách sinh viên - {selectedSubject.name} - {selectedRoom.name} - Ca {selectedSession}
              </h3>
            </div>
            {/* ... existing table structure ... */}
          </div>
        )}
      </div>
    </div>
  );
}

export default GiangVienDashboard;
