import React, { useState, useEffect } from 'react';

function DanhSachKhoa() {
  const [departments, setDepartments] = useState([]);

  // Fetch departments from API
  useEffect(() => {
    fetch('https://apiwebsa.onrender.com/api/khoa')
      .then((response) => response.json())
      .then((data) => setDepartments(data))
      .catch((error) => console.error('Error fetching departments:', error));
  }, []);

  return (
    <div className="max-w-6xl mx-auto p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold text-center mb-6">Danh Sách Các Khoa</h1>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white shadow-md rounded-lg">
          <thead className="bg-blue-500 text-white">
            <tr>
              <th className="px-6 py-3">Mã Khoa</th>
              <th className="px-6 py-3">Tên Khoa</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {departments.map((department, index) => (
              <tr key={department.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 text-center">{department.khoa_id}</td>
                <td className="px-6 py-4">{department.tenkhoa}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default DanhSachKhoa;
